    public void Blue_RibbonMouseDragged(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Blue_RibbonMouseDragged
        // TODO add your handling code here:
        int x = evt.getXOnScreen();
        int y = evt.getYOnScreen();
        this.setLocation(x - xx, y - xy);
    }//GEN-LAST:event_Blue_RibbonMouseDragged

