    public void Generate_buttonMousePressed(java.awt.event.MouseEvent evt) throws Exception {//GEN-FIRST:event_Generate_buttonMousePressed
        // TODO add your handling code here:
        if(filepath.isEmpty()) Update_buttonMousePressed(evt);
        else{
        try {
            Logic.main(null);
        } catch (InvalidFormatException | IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }
    }//GEN-LAST:event_Generate_buttonMousePressed
